import React from "react";
import { Link, Routes, Route } from "react-router-dom";
import './App.css';
// Commenting TeamsPage for now to isolate the cause of error
// import TeamsPage from "./teams-page/TeamsPage.jsx";
import TasksPage from "./TasksPage.jsx";

function App() {
  // Temporary placeholder TeamsPage to avoid crash while debugging
  const SafeTeamsPage = () => (
    <div style={{ padding: "2rem" }}>
      <h2>Your Teams</h2>
      <p>This is a safe placeholder while we debug the modals.</p>
    </div>
  );

  return (
    <div className="App">
      <nav>
        <ul>
          <li>
            <Link to="/">Home</Link>
          </li>
          <li>
            <Link to="/tasks">Tasks</Link>
          </li>
          <li>
            <Link to="/teams">Teams</Link>
          </li>
        </ul>
      </nav>

      <Routes>
        <Route path="/" element={<h1>Welcome to your Home Page!</h1>} />
        <Route path="/tasks" element={<TasksPage />} />
        <Route path="/teams" element={<SafeTeamsPage />} />
        <Route path="*" element={<h2>404 - Page Not Found</h2>} />
      </Routes>
    </div>
  );
}

export default App;
