import React from "react";
import "./Signup.css";
import logo from "./assets/gobearlogo.png";
import { getAuth, createUserWithEmailAndPassword } from "firebase/auth";
import firebaseApp from "./Firebase.js";
import { getDatabase, ref, set } from "firebase/database";
import { useState } from "react";

function Signup() {

  const auth = getAuth(firebaseApp);
  const [fname, setFName] = useState("")
  const [lname, setLName] = useState("")
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState(null);

  const handleSignup = (e) => {
    e.preventDefault();

    createUserWithEmailAndPassword(auth, email, password, fir)
      .then((userCredential) => {
        const user = userCredential.user;

        const db = getDatabase();
        set(ref(db, 'users/' + user.uid), {
          email: user.email,
          firstName: firstName.name,
          // add more fields here (like name) if you want
        });

        // Optionally redirect or show success
      })
      .catch((error) => {
        console.error(error.message);
        // Optionally show error to user
      });
  };



  return (
  <div id="body">
    <div id="title-cont">
      <img src={logo} alt="GoBear Logo" />
    </div>

    <div id="form-cont">
      <h1>Sign Up</h1>
      <form onSubmit={handleSignup}>
        <label htmlFor="fname">Firstname:</label>
        <input
          type="text"
          id="fname"
          name="fname"
          placeholder="First Name"
          value ={fname}
          onChange={(e) => setFName(e.target.value)}
          required
        />
        <br />
        <label htmlFor="lname">Last Name:</label>
        <input
          type="text"
          id="lname"
          name="lname"
          placeholder="Last Name"
          value={lname}
          onChange={(e) => setLName(e.target.value)}
          required
        />
        <br />
        <label htmlFor="email">Email:</label>
        <input
          type="email"
          id="email"
          name="email"
          placeholder="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
        />
        <br />
        <label htmlFor="pword">Password:</label>
        <input
          type="password"
          id="pword"
          name="pword"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
        />
        <br />
        <label htmlFor="pword2">Password:</label>
        <input
          type="password"
          id="pword2"
          name="pword2"
          placeholder="Confirm Password"
          required
        />
        <br />
        <input type="submit" value="Sign Up" />
        {error && <p className="error">{error}</p>}
      </form>

      <a href="./Login" className="button-link">
        Back To Login
      </a>
    </div>
  </div>
  );
}

export default Signup;
