import React from "react";
import "./Login.css";
import logo from "./assets/gobearlogo.png";
import googleicon from "./assets/Google Icon.png";
import githubicon from "./assets/GitHub Icon.png";
import linkedinicon from "./assets/LinkedIn Icon.png";
import { getAuth, signInWithEmailAndPassword } from "firebase/auth";
import firebase from "./Firebase.js";
import { useState } from "react";

function Login() {

  const auth = getAuth(firebase);
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState(null);

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      await auth.signInWithEmailAndPassword(email, password)
    }
    catch (error){
      setError(error.message)
    }
  }

  return (
    <div id="body">
      <div id="title-cont">
        <img src={logo} alt="GoBear Logo" />
      </div>

      <div id="form-cont">
        <h1>Login</h1>
        <form>
          <label for="email">Email:</label>
          <input
            type="email"
            id="email"
            name="email"
            placeholder="Email"
            required
          />
          <br />
          <label for="pword">Password:</label>
          <input
            type="password"
            id="pword"
            name="pword"
            placeholder="Password"
            required
          />
          <br />
          <button id="sign-in" onClick={loginUser}></button>
        </form>

        <a href="./Resetpass">Forgot Password?</a>

        <p>or continue with</p>
        <img src={googleicon} alt="Continue with Google" />
        <img src={githubicon} alt="Continue with GitHub" />
        <img src={linkedinicon} alt="Continue with LinkedIn" />
        <br />

        <p>Don't have an account?</p>
        <a href="./Signup" class="button-link">
          Sign Up
        </a>
      </div>
    </div>
  );
}

export default Login;
